import React from "react";
import { Label, GrupoInput, LeyendaError, IconoValidacion } from '../elementos/formularios';
import { faCircleCheck, faTimesCircle } from '@fortawesome/free-solid-svg-icons';
import Select from "react-select";
const InputSelect = ({ estado, cambiarEstado, label, name, leyendaError, expresionRegular }) => {
    // const onChange = (e) => {
    //     cambiarEstado({ ...estado, campo: e.target.value });
    // }

    const onDropdownChange = (e) => {
        cambiarEstado({ ...estado, campo: e.target.value });
    }

    const options = [
        { value: '1', label: 'ELECTRODOMESTICOS' },
        { value: '2', label: 'HERRAMIENTAS' },
        { value: '3', label: 'PARA EL HOGAR' }
    ]

    const validacion = () => {
        if (expresionRegular) {
            if (expresionRegular.test(estado.campo)) {
                cambiarEstado({ ...estado, valido: 'true' });
            } else {
                cambiarEstado({ ...estado, valido: 'false' });
            }
        }
    }

    return (
        <div>
            <Label htmlFor={name} valido={estado.valido} >{label}</Label>
            <GrupoInput>
                {/* <Input 
                    type={tipo} 
                    placeholder={placeholder} 
                    id={name} 
                    value={estado.campo}
                    onChange={onChange}
                    onKeyUp={validacion}
                    onBlur={validacion}
                    valido={estado.valido}
                /> */}
                <Select
                    options={options}
                    id={name}
                    value={estado.campo}
                    onChange={onDropdownChange}
                    onKeyUp={validacion}
                    onBlur={validacion}
                    valido={estado.valido}
                />
                <IconoValidacion
                    icon={estado.valido === 'true' ? faCircleCheck : faTimesCircle}
                    valido={estado.valido}

                />
            </GrupoInput>
            <LeyendaError valido={estado.valido}>{leyendaError}</LeyendaError>
        </div>
    );
}
export default InputSelect;
