import styled from 'styled-components';

const colores = {
    borde: "#0075FF",
    error: "#bb2929",
    exito: "#1ed12d"
}
const Formulario = styled.form`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px; 

  @media(max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;
const Label = styled.label`
  display: block;
  font-weight: bold;
  padding: 10px;
  min-height: 40px;
`;

const GrupoInput = style.div`
  position: relative;
  z-index: 90;
`;

const Input = styled.input`
  width : 100%;
  background: #fff;
  border-radius: 3px;
  height: 45px;
  line-height: 45px;
  padding: 0 40px;
`;

export {Formulario, Label, GrupoInput};
