import React, {useState} from "react";
import { Formulario, ContenedorBotonCentrado, Boton, MensajeExito, MensajeError } from "./elementos/formularios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';
import Input from "./componentes/Input";
const App = () => {
  const [nombre, cambiarNombre]= useState({campo: '', valido: null});
  const [precio, cambiarPrecio]= useState({campo: '', valido: null});
  const [stock, cambiarStock]= useState({campo: '', valido: null});
  const [fecha, cambiarFecha]= useState({campo: '', valido: null});
  const [marca, cambiarMarca]= useState({campo: '', valido: null});

  return (
    <main>
      <header className="containerHeader">
        <h1 className="titulo">Productos</h1>
      </header>
      <div className="containerFormulario">
        <Formulario action="">

          <Input
            estado = {nombre}
            cambiarEstado= {cambiarNombre}
            tipo="text"
            label="Nombre:"
            placeholder="Nombre"
            name="nombre"
            leyendaError="El nombre no puede tener caracteres especiales como: !@#$%^*(){}"
            expresionRegular=""
          />



          {false && <MensajeError>
            <p>
              <FontAwesomeIcon icon={faExclamationTriangle}/>
              <b>Error:</b> Por favor rellene el formulario correctamente  
            </p>  
          </MensajeError>}
          <ContenedorBotonCentrado>
            <Boton type="submit">Enviar</Boton>
            <MensajeExito>El formulario se envio correctamente!</MensajeExito>
          </ContenedorBotonCentrado>
          {/*  
          <div className="campo">
            <Label htmlFor="">Precio: </Label>
            <input type="text" placeholder="estado" />
          </div>
          <div className="campo">
            <Label className="fecha" htmlFor="">Stock: </Label>
            <input type="date" placeholder="Fecha" />
          </div>
          <div className="campo">
            <Label htmlFor="">Fecha: </Label>
            <input type="text" placeholder="Usuario" />
          </div>
          <div className="campo">
            <Label htmlFor="">Marca: </Label>
            <input type="number" placeholder="SRI" />
          </div>
          */}
        </Formulario>
      </div>
    </main>
  );
}

export default App;
