import React, { useContext } from "react";
import { DataContext } from "../../contexto/DataProvider";
import axios from "axios";

class ProductoTable extends React.Component {

    state = {
        producto: [],
        modalInsertar: false,
        modalEliminar: false,
        form: {
            cod_producto: '',
            id_tipo_prod: '',
            nombre: '',
            precio: '',
            stock: '',
            categoria: '',
            marca: '',
            tipoModal: ''
        }
    }

    peticionGet = () => {
        axios.get("http://localhost:8080/productos").then(response => {
            this.setState({ producto: response.data });
        }).catch(error => {
            console.log(error.message);
        })
    }

    peticionPost = async () => {
        await axios.post("http://localhost:8080/productos", this.state.form).then(response => {
            this.modalInsertar();
            this.peticionGet();
        }).catch(error => {
            console.log(error.message);
        })
    }

    componentDidMount() {
        this.peticionGet();
    }

}

export const ProductoItem = ({ id, Tipo, Nombre, Precio, Imagen, Cantidad, Marca }) => {

    const value = useContext(DataContext);
    const AddCarrito = value.AddCarrito;

    return (
        <div className="producto">

            {
                this.state.producto.map((producto) => (

                    <div className="producto_Img">
                        <img src={Imagen} alt={Nombre} />
                  
                <div className="producto_Datos">
                    <h1>{Nombre}</h1>
                    <p>{Tipo}</p>
                    <p className="price">${Precio}</p>
                    <p>Cantidad: {Cantidad}</p>
                </div>
                </div>
                ))}
            <div className="button">
                <button onClick={() => AddCarrito(id)} className="btn" >
                    Añadir al carro
                </button>
            </div>

            <div className="buttonV">


            </div>
        </div>
    );
}
export default ProductoTable;
