import React, { useState } from "react";
import { Formulario, ContenedorBotonCentrado, Boton, MensajeExito, MensajeError, DivContenedor, Input, Encabezado, Enlace, Navegador, Label } from "./../elementos/formularios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExclamationTriangle, faCartShopping, faI } from '@fortawesome/free-solid-svg-icons';
// import Input from "./../componentes/Input";
import InputSelect from "./InputSelect";

class InputText extends React.Component {
  constructor(props) {
    super(props);
    this.actualizarState = this.actualizarState.bind(this);
    this.state = {
      value:"",
      error:false,
      mensajeError:""
    };
  }

  actualizarState(e) {

  }


  render() {

    return (
      <div>
        <Label htmlFor={"id-" + this.props.name}>{this.props.label}</Label>
        <Input
          id={"id-" + this.props.name}
          type="text"
          name={this.props.name}
          placeholder={this.props.placeholder}
          onChange={this.actualizarState}
        />
        {
          this.state.error ? (
            <p>{this.state.mensajeError}</p>
          ):("")
        }
      </div>
    )
  }
}

class FormularioProductos extends React.Component {
  constructor(props) {
    super(props);
    this.submit = this.submit.bind(this);
    this.actualizarState = this.actualizarState.bind(this);
  }

  actualizarState(e) {

  }

  submit(e) {

  }

  render() {
    return (
      <main>
        <Encabezado>
          <h1 className="titulo">AGREGAR PRODUCTO</h1>
        </Encabezado>
        <Navegador>
          <Enlace href="#"><FontAwesomeIcon icon={faI} /> Inicio</Enlace>

          <Enlace href="#"><FontAwesomeIcon icon={faCartShopping} /> Carrito</Enlace>
        </Navegador>
        <DivContenedor>
          <Formulario onSubmit={this.submit}>

            <InputText
              label="Nombre"
              name="nombre"
              placeholder="nombre"
              actualizarState={this.actualizarState}

            />
            <ContenedorBotonCentrado>
              <Boton type="submit">
                Enviar
              </Boton>
            </ContenedorBotonCentrado>
          </Formulario>
        </DivContenedor>
      </main>
    )
  }
}
