import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrashCan, faPenToSquare } from '@fortawesome/free-solid-svg-icons';
import FormularioClientes from "./Formulario_Cliente";

class ClienteTable extends React.Component {
    constructor(props) {
        this.super(props);
        this.state = {
            cliente: [],
            form: {
                cod_cliente: '',
                cedula: '',
                nombres: '',
                apellidos: '',
                telefono: '',
                direccion: ''
            }
        }
    }
    

    componentDidMount() {

        axios.get("http://localhost:8080/clientes")

            .then(response => response.data)
            .then((data => {
                console.log(data)
                this.setState({ cliente: data });
            }
            ))
    }

    seleccionarCliente = (cliente) => {
        this.setState({
            tipoModal: 'actualizar',
            form: {
                cod_cliente: cliente.cod_cliente,
                cedula: cliente.cedula,
                nombres: cliente.nombres,
                apellidos: cliente.apellidos,
                telefono: cliente.telefono,
                direccion: cliente.direccion
            }
        })
    }

    render() {
        return (
            <>
                <div className="container">
                    <table className="table caption-top">
                        <caption>Clientes {"  "}
                            <button type="button" className="btn btn-success" ><FontAwesomeIcon icon={faPlus} /></button>
                        </caption>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Cedula</th>
                                <th scope="col">Nombre</th>
                                <th scope="col">Apellido</th>
                                <th scope="col">Direccion</th>
                                <th scope="col">Telefono</th>
                                <th scope="col">Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.cliente.map((cliente) => (
                                    <tr>
                                        <td>{cliente.cod_cliente}</td>
                                        <td>{cliente.cedula}</td>
                                        <td>{cliente.nombres}</td>
                                        <td>{cliente.apellidos}</td>
                                        <td>{cliente.telefono}</td>
                                        <td>{cliente.direccion}</td>
                                        <td>
                                            <button type="button" className="btn btn-warning" onClick={() => this.seleccionarCliente(cliente)}> <Link to={"/clientes/Formulario_Cliente_Editar"}><FontAwesomeIcon icon={faPenToSquare} /></Link>  </button>
                                            {" "}
                                            <button type="button" className="btn btn-danger"><FontAwesomeIcon icon={faTrashCan} /></button>
                                        </td>
                                    </tr>
                                ))}
                        </tbody>
                    </table>
                </div>

            </>
        );

    }
}

export default ClienteTable;
