import React from "react";
import {Label, GrupoInput, LeyendaError, IconoValidacion, Input} from './../elementos/formularios';
import { faCircleCheck } from '@fortawesome/free-solid-svg-icons';
const ComponenteInput = ({estado, cambiarEstado, tipo, label, placeholder, name, leyendaError, expresionRegular}) => {
    const onChange = (e) => {
        cambiarEstado({...estado, campo: e.target.value });
    }
    return (
        <div className="campo">
            <Label htmlFor={name}>{label}</Label>
            <GrupoInput>
                <Input 
                    type={tipo} 
                    placeholder={placeholder} 
                    id={name} 
                    value={estado.campo}
                    onChange={onChange}
                />
                <IconoValidacion icon={faCircleCheck} />
            </GrupoInput>
            <LeyendaError>{leyendaError}</LeyendaError>
        </div>
    );
}
export default ComponenteInput;
