import React from "react";
import axios from "axios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrashCan, faPenToSquare } from '@fortawesome/free-solid-svg-icons';



class ClienteTable extends React.Component {
    state = {
        producto: [],
        modalInsertar: false,
        modalEliminar: false,
        form: {
            cod_cliente: '',
            cedula: '',
            nombres: '',
            apellidos: '',
            telefono: '',
            direccion: '',
            tipoModal: ''
        }
    }

    peticionGet = () => {
        axios.get("http://localhost:8080/productos").then(response => {
            this.setState({ cliente: response.data });
        }).catch(error => {
            console.log(error.message);
        })
    }

    peticionPost = async () => {
        await axios.post("http://localhost:8080/productos", this.state.form).then(response => {
            this.modalInsertar();
            this.peticionGet();
        }).catch(error => {
            console.log(error.message);
        })
    }

    peticionPut = () => {
        axios.put("http://localhost:8080/productos/" + this.state.form.cod_cliente, this.state.form).then(response => {
            this.modalInsertar();
            this.peticionGet();
        })
    }

    peticionDelete = () => {
        axios.delete("http://localhost:8080/productos/" + this.state.form.cod_cliente).then(response => {
            this.setState({ modalEliminar: false });
            this.peticionGet();
        }).catch(error => {
            console.log(error.message);
        })
    }

    modalInsertar = () => {
        this.setState({ modalInsertar: !this.state.modalInsertar });
    }

    handleChange = async e => {
        e.persist();
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);
    }

    seleccionarCliente = (cliente) => {
        this.setState({
            tipoModal: 'actualizar',
            form: {
                cod_cliente: cliente.cod_cliente,
                cedula: cliente.cedula,
                nombres: cliente.nombres,
                apellidos: cliente.apellidos,
                telefono: cliente.telefono,
                direccion: cliente.direccion
            }
        })
    }

    componentDidMount() {
        this.peticionGet();
    }

    render() {
        return (
            <>
                <br />
                <br />
                <br />
                <br />
                <div className="container">
                    <table className="table caption-top">
                        <caption>Productos {"  "}
                            <button type="button" className="btn btn-success" onClick={() => this.MostrarModalInsertar()} ><FontAwesomeIcon icon={faPlus} /></button>
                        </caption>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">ID Tipo</th>
                                <th scope="col">Nombre</th>
                                <th scope="col">Precio</th>
                                <th scope="col">Stock</th>
                                <th scope="col">Marca</th>
                                <th scope="col">Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.producto.map((producto) => (
                                    <tr>
                                        <td>{producto.cod_producto}</td>
                                        <td>{producto.id_tipo_prod}</td>
                                        <td>{producto.nombre}</td>
                                        <td>{producto.precio}</td>
                                        <td>{producto.stock}</td>
                                        <td>{producto.marca}</td>
                                        <td>
                                            <button type="button" className="btn btn-warning"><FontAwesomeIcon icon={faPenToSquare} /></button>
                                            {" "}
                                            <button type="button" className="btn btn-danger"><FontAwesomeIcon icon={faTrashCan} /></button>
                                        </td>
                                    </tr>
                                ))}
                        </tbody>
                    </table>
                </div>

            </>
        );

    }

}

export default ProductoTable;
