import React from "react";
import { Formulario, ContenedorBotonCentrado, Boton, LeyendaError, MensajeError, DivContenedor, Input, Encabezado, Enlace, Navegador, Label } from "./../elementos/formularios";
import './../estilos.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faI } from '@fortawesome/free-solid-svg-icons';
// import Input from "./../componentes/Input";
import { esNombre, esCedula, esDireccion, esTelefono } from "./Validaciones";


class InputText extends React.Component {
  constructor(props) {
    super(props);
    this.actualizarState = this.actualizarState.bind(this);
    this.state = {
      value: "",
      error: false,
      mensajeError: ""
    };
  }

  actualizarState(e) {
    const { name, value } = e.target;
    console.log(this.props.validacion(value));

    if (this.props.validacion(value)) {
      this.setState({
        value,
        error: false,
        mensajeError: ""
      });
      this.props.actualizarState({
        name, value, error: false
      });
    } else {
      this.setState({
        value,
        error: true,
        mensajeError: this.props.mensajeError
      });
      this.props.actualizarState({
        name, value, error: true
      });
    }
  }


  render() {

    return (
      <div>
        <Label htmlFor={"id-" + this.props.name}>{this.props.label}</Label>
        <Input
          id={"id-" + this.props.name}
          type="text"
          name={this.props.name}
          placeholder={this.props.placeholder}
          onChange={this.actualizarState}
        />
        {
          this.state.error ? (
            <LeyendaError>{this.state.mensajeError}</LeyendaError>
          ) : ("")
        }
      </div>
    )
  }
}

class FormularioClientes extends React.Component {
  constructor(props) {
    super(props);
    this.submit = this.submit.bind(this);
    this.actualizarState = this.actualizarState.bind(this);
    this.state = {
      nombre: {
        value: "",
        error: true
      },
      direccion: {
        value: "",
        error: true
      },
      cedula: {
        value: "",
        error: true
      },
      telefono: {
        value: "",
        error: true
      }
    }
  }




  actualizarState(input) {
    this.setState({
      ...this.state,
      [input.name]: {
        value: input.value,
        error: input.error
      }
    }, () => { console.log(this.state); });

  }

  submit(e) {

  }

  render() {
    return (
      <main>
        <Encabezado>
          <h1 className="titulo">AGREGAR PRODUCTO</h1>
        </Encabezado>
        <Navegador>
          <Enlace href="#"><FontAwesomeIcon icon={faI} /> Inicio</Enlace>

          <Enlace href="#"><FontAwesomeIcon icon={faCartShopping} /> Carrito</Enlace>
        </Navegador>
        <DivContenedor>
          <Formulario onSubmit={this.submit}>
            <InputText
              label="Nombre"
              name="nombre"
              placeholder="nombre"
              validacion={esNombre}
              mensajeError="El campo no puede estar vacio, el texto no debe contener caracteres especiales como: !@#$%^*(){}"
              actualizarState={this.actualizarState}
            />
            <InputText
              label="Apellido"
              name="apellido"
              placeholder="apellido"
              validacion={esNombre}
              mensajeError="El campo no puede estar vacio, el texto no debe contener caracteres especiales como: !@#$%^*(){}"
              actualizarState={this.actualizarState}
            />
            <InputText
              label="Cedula"
              name="cedula"
              placeholder="0923377972"
              validacion={esCedula}
              mensajeError="El campo no puede quedar vacio y debe ingresar 10 numeros"
              actualizarState={this.actualizarState}
            />
            <InputText
              label="Direccion"
              name="direccion"
              placeholder="Direccion del cliente"
              validacion={esDireccion}
              mensajeError="El campo no puede quedar vacio"
              actualizarState={this.actualizarState}
            />
            <InputText
              label="Telefono"
              name="telefono"
              placeholder="Telefono del cliente"
              validacion={esTelefono}
              mensajeError="El campo no puede quedar vacio"
              actualizarState={this.actualizarState}
            />
            {
            this.state.cedula.value === "" || this.state.nombre ==="" ? (
              <MensajeError>
                <p>
                  <b>Error:</b> Por favor rellene el formulario correctamente
                </p>
              </MensajeError>
            ) : ("")
            }

            <ContenedorBotonCentrado>
              <Boton type="submit" disabled={this.state.nombre.error
                || this.state.cedula.error || this.state.direccion.error || this.state.telefono.error}>
                Enviar
              </Boton>
            </ContenedorBotonCentrado>
          </Formulario>
        </DivContenedor>
      </main>
    )
  }
}
export default FormularioClientes;
