import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrashCan, faPenToSquare } from '@fortawesome/free-solid-svg-icons';
import FormularioClientes from "./Formulario_Cliente";

const url="http://localhost:8080/clientes";

class ClienteTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cliente: [],
            form: {
                cod_cliente: '',
                cedula: '',
                nombres: '',
                apellidos: '',
                telefono: '',
                direccion: ''
            }
        }
    }
    
    peticionGet=()=>{
        axios.get(url).then(response=>{
          this.setState({data: response.data});
        }).catch(error=>{
          console.log(error.message);
        })
        }
        
        peticionPost=async()=>{
          delete this.state.form.id;
         await axios.post(url,this.state.form).then(response=>{
            this.modalInsertar();
            this.peticionGet();
          }).catch(error=>{
            console.log(error.message);
          })
        }
        
        peticionPut=()=>{
          axios.put(url+this.state.form.id, this.state.form).then(response=>{
            this.modalInsertar();
            this.peticionGet();
          })
        }
        
        peticionDelete=()=>{
          axios.delete(url+this.state.form.id).then(response=>{
            this.setState({modalEliminar: false});
            this.peticionGet();
          })
        }
        
        modalInsertar=()=>{
          this.setState({modalInsertar: !this.state.modalInsertar});
        }

    handleChange = async e => {
        e.persist();
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value
          }
        });
        console.log(this.state.form);
      }

    seleccionarCliente = (cliente) => {
        this.setState({
            tipoModal: 'actualizar',
            form: {
                cod_cliente: cliente.cod_cliente,
                cedula: cliente.cedula,
                nombres: cliente.nombres,
                apellidos: cliente.apellidos,
                telefono: cliente.telefono,
                direccion: cliente.direccion
            }
        })
    }

    render() {
        return (
            <>
                <div className="container">
                    <table className="table caption-top">
                        <caption>Clientes {"  "}
                            <button type="button" className="btn btn-success" ><FontAwesomeIcon icon={faPlus} /></button>
                        </caption>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Cedula</th>
                                <th scope="col">Nombre</th>
                                <th scope="col">Apellido</th>
                                <th scope="col">Direccion</th>
                                <th scope="col">Telefono</th>
                                <th scope="col">Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.cliente.map((cliente) => (
                                    <tr>
                                        <td>{cliente.cod_cliente}</td>
                                        <td>{cliente.cedula}</td>
                                        <td>{cliente.nombres}</td>
                                        <td>{cliente.apellidos}</td>
                                        <td>{cliente.telefono}</td>
                                        <td>{cliente.direccion}</td>
                                        <td>
                                            <button type="button" className="btn btn-warning" onClick={() => this.seleccionarCliente(cliente)}> <Link to={"/clientes/Formulario_Cliente_Editar"}><FontAwesomeIcon icon={faPenToSquare} /></Link>  </button>
                                            {" "}
                                            <button type="button" className="btn btn-danger"><FontAwesomeIcon icon={faTrashCan} /></button>
                                        </td>
                                    </tr>
                                ))}
                        </tbody>
                    </table>

                    <Modal isOpen={this.state.modalInsertar}>
                <ModalHeader style={{display: 'block'}}>
                  <span style={{float: 'right'}} onClick={()=>this.modalInsertar()}>x</span>
                </ModalHeader>
                <ModalBody>
                  <div className="form-group">
                  <input
              label="ID"
              name="cod_cliente"
              placeholder="ID"
             //readOnly
              value={this.state.form.cod_cliente}
              onChange={this.handleChange}
            />
            <input
              label="Cedula"
              name="cedula"
              placeholder="0923377972"
              value={this.state.form.cedula}
              mensajeError="El campo no puede quedar vacio y debe ingresar 10 numeros"
              onChange={this.handleChange}
            // actualizarState={this.actualizarState}
            />
            <input
              label="Nombre"
              name="nombres"
              placeholder="nombre"
              value={this.state.form.nombres}
              mensajeError="El campo no puede estar vacio, el texto no debe contener caracteres especiales como: !@#$%^*(){}"
              onChange={this.handleChange}
            //  actualizarState={this.actualizarState}
            />
            <input
              label="Apellido"
              name="apellidos"
              placeholder="apellido"
              value={this.state.form.apellidos}
              mensajeError="El campo no puede estar vacio, el texto no debe contener caracteres especiales como: !@#$%^*(){}"
              onChange={this.handleChange}
            // actualizarState={this.actualizarState}
            />
            <input
              label="Telefono"
              name="telefono"
              placeholder="Telefono del cliente"
              value={this.state.form.telefono}
              mensajeError="El campo no puede quedar vacio"
              onChange={this.handleChange}
            // actualizarState={this.actualizarState}
            />
            <input
              label="Direccion"
              name="direccion"
              placeholder="Direccion del cliente"
              value={this.state.form.direccion}
              mensajeError="El campo no puede quedar vacio"
              onChange={this.handleChange}
            //actualizarState={this.actualizarState}
            />
                  </div>
                </ModalBody>

                <ModalFooter>
                  {this.state.tipoModal=='insertar'?
                    <button className="btn btn-success" onClick={()=>this.peticionPost()}>
                    Insertar
                  </button>: <button className="btn btn-primary" onClick={()=>this.peticionPut()}>
                    Actualizar
                  </button>
  }
                    <button className="btn btn-danger" onClick={()=>this.modalInsertar()}>Cancelar</button>
                </ModalFooter>
          </Modal>

                </div>

            </>
        );

    }
}

export default ClienteTable;
